import React from 'react';
import {
  Card,
  CardHeader,
  CardContent,
  Divider,
  Button
} from '@mui/material';
import { ChevronRight } from 'lucide-react';

const SectionCard = ({ 
  title, 
  actionText, 
  onActionClick, 
  children, 
  ...props 
}) => {
  return (
    <Card 
      elevation={0} 
      sx={{ 
        background: 'linear-gradient(135deg, #ffffff 0%, #f8fafc 100%)',
        border: '1px solid', 
        borderColor: 'divider', 
        borderRadius: 3,
        height: '100%',
        position: 'relative',
        overflow: 'hidden',
        transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
        '&:hover': {
          transform: 'translateY(-4px)',
          boxShadow: '0 20px 40px rgba(0,0,0,0.08), 0 0 0 1px rgba(103, 58, 183, 0.05)',
          borderColor: 'primary.light',
          '&::before': {
            opacity: 1
          }
        },
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          height: '3px',
          background: 'linear-gradient(90deg, #667eea, #764ba2)',
          opacity: 0,
          transition: 'opacity 0.3s ease'
        }
      }}
      {...props}
    >
      <CardHeader 
        title={title} 
        action={
          actionText && (
            <Button 
              variant="text" 
              endIcon={<ChevronRight size={16} />}
              size="small"
              onClick={onActionClick}
              sx={{ 
                transition: 'all 0.2s ease',
                '&:hover': {
                  transform: 'translateX(3px)',
                  color: 'primary.main'
                }
              }}
            >
              {actionText}
            </Button>
          )
        }
      />
      <Divider />
      <CardContent>
        {children}
      </CardContent>
    </Card>
  );
};

export default SectionCard;
