import React from 'react';
import {
  Card,
  CardContent,
  Stack,
  Typography,
  Avatar,
  Button
} from '@mui/material';
import { ChevronRight } from 'lucide-react';

const SummaryCard = ({ 
  title, 
  value, 
  icon, 
  color = 'primary', 
  subtitle, 
  subtitleIcon,
  buttonText,
  onButtonClick
}) => {
  return (
    <Card 
      elevation={0} 
      sx={{ 
        background: 'linear-gradient(135deg, #ffffff 0%, #f8fafc 100%)',
        border: '1px solid', 
        borderColor: 'divider',
        borderRadius: 3,
        position: 'relative',
        overflow: 'hidden',
        transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
        '&:hover': {
          transform: 'translateY(-8px) scale(1.02)',
          boxShadow: `0 20px 40px rgba(0,0,0,0.1), 0 0 0 1px rgba(${color === 'primary' ? '103, 58, 183' : color === 'success' ? '76, 175, 80' : color === 'warning' ? '255, 152, 0' : '244, 67, 54'}, 0.1)`,
          borderColor: `${color}.main`,
          '&::before': {
            opacity: 1
          }
        },
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          height: '4px',
          background: `linear-gradient(90deg, ${color === 'primary' ? '#667eea, #764ba2' : color === 'success' ? '#4ade80, #22c55e' : color === 'warning' ? '#fbbf24, #f59e0b' : '#f87171, #ef4444'})`,
          opacity: 0,
          transition: 'opacity 0.3s ease'
        }
      }}
    >
      <CardContent>
        <Stack direction="row" justifyContent="space-between" alignItems="center" sx={{ mb: 2 }}>
          <Typography color="text.secondary" variant="subtitle2">
            {title}
          </Typography>
          <Avatar 
            sx={{ 
              background: `linear-gradient(135deg, ${color === 'primary' ? '#667eea, #764ba2' : color === 'success' ? '#4ade80, #22c55e' : color === 'warning' ? '#fbbf24, #f59e0b' : '#f87171, #ef4444'})`,
              width: 48, 
              height: 48,
              boxShadow: `0 8px 24px rgba(${color === 'primary' ? '103, 58, 183' : color === 'success' ? '76, 175, 80' : color === 'warning' ? '255, 152, 0' : '244, 67, 54'}, 0.25)`,
              transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
              '&:hover': {
                transform: 'scale(1.15) rotate(5deg)',
                boxShadow: `0 12px 32px rgba(${color === 'primary' ? '103, 58, 183' : color === 'success' ? '76, 175, 80' : color === 'warning' ? '255, 152, 0' : '244, 67, 54'}, 0.4)`
              }
            }}
          >
            {icon}
          </Avatar>
        </Stack>
        <Typography variant="h4" fontWeight="bold">
          {value}
        </Typography>

        {subtitle && (
          <Stack direction="row" spacing={1} alignItems="center" sx={{ mt: 1 }}>
            {subtitleIcon}
            <Typography variant={buttonText ? "caption" : "body2"} color={buttonText ? "text.secondary" : `${color}.main`}>
              {subtitle}
            </Typography>
          </Stack>
        )}

        {buttonText && (
          <Button 
            variant="text" 
            size="small" 
            endIcon={<ChevronRight size={16} />}
            onClick={onButtonClick}
            sx={{ 
              mt: 1, 
              p: 0,
              transition: 'all 0.2s ease',
              '&:hover': {
                transform: 'translateX(5px)',
                color: `${color}.main`
              }
            }}
          >
            {buttonText}
          </Button>
        )}
      </CardContent>
    </Card>
  );
};

export default SummaryCard;
