import React, { useState } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Button,
  Box,
  Chip,
  Stack,
  IconButton,
  Divider,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Badge,
  Paper
} from '@mui/material';
import {
  Check,
  Star,
  Shield,
  Zap,
  Crown,
  ShoppingCart,
  Minus,
  TrendingUp,
  Award,
  Sparkles
} from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

const EnhancedTierCard = ({ 
  tier, 
  isInCart, 
  onAddToCart, 
  onRemoveFromCart,
  getTierColor,
  getTierIcon,
  getTierDescription,
  theme,
  index = 0
}) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [isHovered, setIsHovered] = useState(false);

  const getTierGradient = (tierName) => {
    const name = tierName.toLowerCase();
    if (name.includes('core')) return 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)';
    if (name.includes('plus')) return 'linear-gradient(135deg, #9c27b0 0%, #673ab7 100%)';
    if (name.includes('max')) return 'linear-gradient(135deg, #ff6b6b 0%, #ee5a24 100%)';
    return 'linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)';
  };

  const getTierBadge = (tierName) => {
    const name = tierName.toLowerCase();
    if (name.includes('plus')) return { text: 'Most Popular', color: '#9c27b0' };
    if (name.includes('max')) return { text: 'Best Value', color: '#ff6b6b' };
    return null;
  };

  const getTierCustomIcon = (tierName) => {
    const name = tierName.toLowerCase();
    if (name.includes('core')) return <Shield size={24} color="white" />;
    if (name.includes('plus')) return <Zap size={24} color="white" />;
    if (name.includes('max')) return <Crown size={24} color="white" />;
    return <Star size={24} color="white" />;
  };

  const formatPrice = (price) => {
    if (!price) return '$49.99';
    const numericPrice = price.toString().replace(/[^0-9.]/g, '');
    const parsedPrice = parseFloat(numericPrice);
    if (isNaN(parsedPrice)) return '$49.99';
    return `$${parsedPrice.toFixed(2)}`;
  };

  const badge = getTierBadge(tier.name);
  const isPopular = badge !== null;

  const cardVariants = {
    hidden: { opacity: 0, y: 50, scale: 0.9 },
    visible: { 
      opacity: 1, 
      y: 0, 
      scale: 1,
      transition: {
        duration: 0.6,
        delay: index * 0.15,
        ease: [0.25, 0.46, 0.45, 0.94]
      }
    },
    hover: {
      y: -12,
      scale: 1.03,
      transition: { duration: 0.3, ease: [0.25, 0.46, 0.45, 0.94] }
    }
  };

  const iconVariants = {
    idle: { rotate: 0, scale: 1 },
    hover: { rotate: 10, scale: 1.1, transition: { duration: 0.3 } }
  };

  return (
    <motion.div
      variants={cardVariants}
      initial="hidden"
      animate="visible"
      whileHover="hover"
      style={{ height: '100%' }}
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
    >
      <Card
        sx={{
          height: '100%',
          display: 'flex',
          flexDirection: 'column',
          cursor: 'pointer',
          borderRadius: 4,
          bgcolor: '#1a1a1a',
          border: isPopular ? '3px solid #9c27b0' : isInCart ? '2px solid #4caf50' : '1px solid #333',
          overflow: 'hidden',
          position: 'relative',
          boxShadow: isPopular 
            ? '0 20px 40px rgba(156, 39, 176, 0.4)' 
            : isInCart 
              ? '0 16px 32px rgba(76, 175, 80, 0.3)'
              : '0 8px 24px rgba(0,0,0,0.4)',
          transition: 'all 0.3s ease',
        }}
      >
        {/* Popular Badge */}
        {isPopular && (
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.5 + index * 0.1 }}
          >
            <Box
              sx={{
                position: 'absolute',
                top: -1,
                left: '50%',
                transform: 'translateX(-50%)',
                bgcolor: badge.color,
                color: 'white',
                px: 3,
                py: 0.5,
                borderRadius: '0 0 16px 16px',
                fontSize: '0.75rem',
                fontWeight: 'bold',
                zIndex: 10,
                boxShadow: '0 4px 12px rgba(0,0,0,0.3)'
              }}
            >
              <Stack direction="row" spacing={0.5} alignItems="center">
                <Star size={12} fill="white" />
                {badge.text}
              </Stack>
            </Box>
          </motion.div>
        )}

        {/* Header with Gradient */}
        <Box
          sx={{
            background: getTierGradient(tier.name),
            p: 3,
            pb: 4,
            position: 'relative',
            overflow: 'hidden'
          }}
        >
          {/* Decorative Elements */}
          <Box
            sx={{
              position: 'absolute',
              top: -20,
              right: -20,
              width: 80,
              height: 80,
              borderRadius: '50%',
              bgcolor: 'rgba(255,255,255,0.1)',
            }}
          />
          <Box
            sx={{
              position: 'absolute',
              bottom: -30,
              left: -30,
              width: 60,
              height: 60,
              borderRadius: '50%',
              bgcolor: 'rgba(255,255,255,0.05)',
            }}
          />

          <Stack direction="row" justifyContent="space-between" alignItems="flex-start" sx={{ mb: 2 }}>
            <motion.div variants={iconVariants} animate={isHovered ? "hover" : "idle"}>
              <Paper
                sx={{
                  p: 1.5,
                  borderRadius: 2,
                  bgcolor: 'rgba(255,255,255,0.2)',
                  backdropFilter: 'blur(10px)'
                }}
              >
                {getTierCustomIcon(tier.name)}
              </Paper>
            </motion.div>

            {isInCart && (
              <motion.div
                initial={{ scale: 0 }}
                animate={{ scale: 1 }}
                transition={{ type: "spring", stiffness: 500, damping: 30 }}
              >
                <Chip
                  icon={<Check size={16} />}
                  label="In Cart"
                  size="small"
                  sx={{
                    bgcolor: 'rgba(76, 175, 80, 0.9)',
                    color: 'white',
                    fontWeight: 'bold'
                  }}
                />
              </motion.div>
            )}
          </Stack>

          <Typography variant="h4" fontWeight="bold" color="white" sx={{ mb: 1 }}>
            {tier.name}
          </Typography>

          <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.8)', mb: 3 }}>
            {getTierDescription ? getTierDescription(tier.name) : 'Professional security solution'}
          </Typography>

          {/* Pricing */}
          <Stack direction="row" alignItems="baseline" spacing={1}>
            <Typography variant="h2" fontWeight="bold" color="white">
              {formatPrice(tier.price)}
            </Typography>
            <Typography variant="body1" sx={{ color: 'rgba(255,255,255,0.7)' }}>
              /month
            </Typography>
          </Stack>
        </Box>

        <CardContent sx={{ flexGrow: 1, p: 3 }}>
          {/* Features List */}
          <Typography variant="subtitle2" fontWeight="bold" color="white" sx={{ mb: 2 }}>
            What's Included:
          </Typography>

          <List dense sx={{ mb: 3 }}>
            {tier.features && tier.features.slice(0, isExpanded ? tier.features.length : 5).map((feature, idx) => (
              <motion.div
                key={idx}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.7 + idx * 0.1 }}
              >
                <ListItem sx={{ px: 0, py: 0.5 }}>
                  <ListItemIcon sx={{ minWidth: 28 }}>
                    <Check size={16} color="#4caf50" />
                  </ListItemIcon>
                  <ListItemText 
                    primary={feature}
                    primaryTypographyProps={{
                      variant: 'body2',
                      color: '#ccc',
                      fontWeight: 'medium'
                    }}
                  />
                </ListItem>
              </motion.div>
            ))}
          </List>

          {tier.features && tier.features.length > 5 && (
            <Button
              size="small"
              onClick={() => setIsExpanded(!isExpanded)}
              sx={{ 
                color: '#9c27b0', 
                textTransform: 'none',
                fontWeight: 'bold',
                mb: 2
              }}
            >
              {isExpanded ? 'Show Less' : `+${tier.features.length - 5} More Features`}
            </Button>
          )}

          {/* Benefits Section */}
          <Paper
            sx={{
              p: 2,
              bgcolor: 'rgba(156, 39, 176, 0.1)',
              border: '1px solid rgba(156, 39, 176, 0.2)',
              borderRadius: 2,
              mb: 3
            }}
          >
            <Stack direction="row" spacing={1} alignItems="center" sx={{ mb: 1 }}>
              <Sparkles size={16} color="#9c27b0" />
              <Typography variant="subtitle2" fontWeight="bold" color="#9c27b0">
                Key Benefits
              </Typography>
            </Stack>
            <Typography variant="body2" color="#bbb">
              {tier.name.includes('Core') && '24/7 monitoring with essential security features'}
              {tier.name.includes('Plus') && 'Advanced threat detection with predictive analytics'}
              {tier.name.includes('Max') && 'Enterprise-grade security with AI-powered insights'}
            </Typography>
          </Paper>
        </CardContent>

        {/* Action Button */}
        <Box sx={{ p: 3, pt: 0 }}>
          <AnimatePresence mode="wait">
            {isInCart ? (
              <motion.div
                key="remove"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.8 }}
                transition={{ duration: 0.2 }}
              >
                <Stack spacing={1}>
                  <Button
                    fullWidth
                    variant="outlined"
                    size="large"
                    startIcon={<Remove size={18} />}
                    onClick={() => onRemoveFromCart(tier.id)}
                    sx={{
                      borderColor: '#f44336',
                      color: '#f44336',
                      borderRadius: 3,
                      py: 1.5,
                      fontWeight: 'bold',
                      textTransform: 'none',
                      '&:hover': {
                        borderColor: '#d32f2f',
                        bgcolor: 'rgba(244, 67, 54, 0.1)'
                      }
                    }}
                  >
                    Remove from Cart
                  </Button>
                  <Typography variant="caption" color="#4caf50" textAlign="center" fontWeight="medium">
                    âœ“ Added to your cart
                  </Typography>
                </Stack>
              </motion.div>
            ) : (
              <motion.div
                key="add"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.8 }}
                transition={{ duration: 0.2 }}
              >
                <Button
                  fullWidth
                  variant="contained"
                  size="large"
                  startIcon={<ShoppingCart size={18} />}
                  onClick={() => onAddToCart(tier)}
                  sx={{
                    bgcolor: '#9c27b0',
                    borderRadius: 3,
                    py: 1.5,
                    fontWeight: 'bold',
                    textTransform: 'none',
                    boxShadow: '0 8px 24px rgba(156, 39, 176, 0.3)',
                    '&:hover': {
                      bgcolor: '#7b1fa2',
                      boxShadow: '0 12px 32px rgba(156, 39, 176, 0.4)',
                      transform: 'translateY(-2px)'
                    },
                    transition: 'all 0.3s ease'
                  }}
                >
                  Add to Cart
                </Button>
              </motion.div>
            )}
          </AnimatePresence>

          <Typography 
            variant="caption" 
            color="#888" 
            textAlign="center" 
            display="block" 
            sx={{ mt: 2 }}
          >
            30-day money-back guarantee
          </Typography>
        </Box>
      </Card>
    </motion.div>
  );
};

export default EnhancedTierCard;
