import React from 'react';
import {
  Card,
  CardContent,
  Typography,
  Button,
  Box,
  Chip,
  Stack,
  Divider,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Paper,
  Grid
} from '@mui/material';
import {
  Check,
  Shield,
  Zap,
  Crown,
  ShoppingCart,
  CheckCircle
} from 'lucide-react';

const EnhancedTierCard = ({ 
  tier, 
  isInCart, 
  onAddToCart, 
  onRemoveFromCart,
  getTierColor: propGetTierColor,
  getTierIcon: propGetTierIcon,
  getTierDescription,
  theme
}) => {
  const getTierIcon = (tierName) => {
    const name = tierName.toLowerCase();
    if (name.includes('core')) return <Shield size={20} color="#1976d2" />;
    if (name.includes('plus')) return <Zap size={20} color="#9c27b0" />;
    if (name.includes('max')) return <Crown size={20} color="#f57c00" />;
    return <Shield size={20} color="#1976d2" />;
  };

  const getTierColor = (tierName) => {
    const name = tierName.toLowerCase();
    if (name.includes('core')) return '#1976d2';
    if (name.includes('plus')) return '#9c27b0';
    if (name.includes('max')) return '#f57c00';
    return '#1976d2';
  };

  const formatPrice = (price) => {
    if (!price) return '$49.99';
    const numericPrice = price.toString().replace(/[^0-9.]/g, '');
    const parsedPrice = parseFloat(numericPrice);
    if (isNaN(parsedPrice)) return '$49.99';
    return `$${parsedPrice.toFixed(2)}`;
  };

  const isPopular = tier.name.toLowerCase().includes('plus');

  return (
    <Grid item xs={12} sm={6} md={4}>
      <Card 
        elevation={isPopular ? 8 : 2}
        sx={{
          height: '100%',
          display: 'flex',
          flexDirection: 'column',
          borderRadius: 2,
          border: isPopular ? '2px solid #9c27b0' : '1px solid #e0e0e0',
          position: 'relative',
          bgcolor: 'white',
          transition: 'all 0.2s ease-in-out',
          '&:hover': {
            transform: 'translateY(-4px)',
            boxShadow: theme.shadows[8]
          }
        }}
      >
        {/* Popular Badge */}
        {isPopular && (
          <Box
            sx={{
              position: 'absolute',
              top: -12,
              left: '50%',
              transform: 'translateX(-50%)',
              bgcolor: '#9c27b0',
              color: 'white',
              px: 3,
              py: 0.5,
              borderRadius: '20px',
              fontSize: '0.75rem',
              fontWeight: 'bold',
              zIndex: 10,
              boxShadow: '0 2px 8px rgba(156, 39, 176, 0.3)'
            }}
          >
            MOST POPULAR
          </Box>
        )}

        {/* Header */}
        <Box sx={{ p: 3, pb: 2, textAlign: 'center' }}>
          <Box sx={{ mb: 2 }}>
            <Paper
              elevation={0}
              sx={{
                width: 60,
                height: 60,
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                bgcolor: `${getTierColor(tier.name)}15`,
                mx: 'auto',
                mb: 2
              }}
            >
              {getTierIcon(tier.name)}
            </Paper>
          </Box>

          <Typography variant="h5" fontWeight="bold" color="text.primary" sx={{ mb: 1 }}>
            {tier.name}
          </Typography>

          <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
            {tier.name.includes('Core') && 'Essential security features for small businesses'}
            {tier.name.includes('Plus') && 'Advanced protection for growing organizations'}
            {tier.name.includes('Max') && 'Enterprise-grade security for large corporations'}
          </Typography>

          {/* Pricing */}
          <Box sx={{ mb: 2 }}>
            <Typography variant="h3" fontWeight="bold" color="text.primary">
              {formatPrice(tier.price)}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              per month
            </Typography>
          </Box>

          {isInCart && (
            <Chip
              icon={<CheckCircle size={16} />}
              label="Added to Cart"
              color="success"
              size="small"
              sx={{ mb: 2 }}
            />
          )}
        </Box>

        <Divider />

        {/* Features */}
        <CardContent sx={{ flexGrow: 1, p: 3 }}>
          <Typography variant="subtitle2" fontWeight="bold" color="text.primary" sx={{ mb: 2 }}>
            What's Included:
          </Typography>

          <List dense sx={{ p: 0 }}>
            {tier.features && tier.features.map((feature, idx) => (
              <ListItem key={idx} sx={{ px: 0, py: 0.5 }}>
                <ListItemIcon sx={{ minWidth: 32 }}>
                  <Check size={16} color="#4caf50" />
                </ListItemIcon>
                <ListItemText 
                  primary={feature}
                  primaryTypographyProps={{
                    variant: 'body2',
                    color: 'text.primary'
                  }}
                />
              </ListItem>
            ))}
          </List>
        </CardContent>

        <Divider />

        {/* Action Button */}
        <Box sx={{ p: 3 }}>
          {isInCart ? (
            <Stack spacing={1}>
              <Button
                fullWidth
                variant="outlined"
                color="error"
                onClick={() => onRemoveFromCart(tier.id)}
                sx={{
                  borderRadius: 2,
                  py: 1.5,
                  fontWeight: 'medium',
                  textTransform: 'none'
                }}
              >
                Remove from Cart
              </Button>
            </Stack>
          ) : (
            <Button
              fullWidth
              variant="contained"
              startIcon={<ShoppingCart size={18} />}
              onClick={() => onAddToCart(tier)}
              sx={{
                bgcolor: getTierColor(tier.name),
                borderRadius: 2,
                py: 1.5,
                fontWeight: 'medium',
                textTransform: 'none',
                '&:hover': {
                  bgcolor: getTierColor(tier.name),
                  filter: 'brightness(0.9)'
                }
              }}
            >
              Add to Cart
            </Button>
          )}

          <Typography 
            variant="caption" 
            color="text.secondary" 
            textAlign="center" 
            display="block" 
            sx={{ mt: 1 }}
          >
            30-day money-back guarantee
          </Typography>
        </Box>
      </Card>
    </Grid>
  );
};

export default EnhancedTierCard;
