import React from 'react';
import { Box, Grid, Typography, Container, Paper, Stack } from '@mui/material';
import EnhancedTierCard from './EnhancedTierCard.tsx';

const TierGrid = ({ 
  tiers, 
  isInCart, 
  onAddToCart, 
  onRemoveFromCart,
  getTierColor,
  getTierIcon,
  getTierDescription,
  theme 
}) => {
  if (!tiers || tiers.length === 0) {
    return (
      <Box sx={{ textAlign: 'center', py: 6 }}>
        <Typography variant="h6" color="text.secondary">
          No tiers available for this sub-bundle.
        </Typography>
      </Box>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      {/* Header Section */}
      <Box sx={{ textAlign: 'center', mb: 6 }}>
        <Typography 
          variant="h4" 
          fontWeight="bold" 
          color="text.primary" 
          sx={{ mb: 2 }}
        >
          Choose Your Security Plan
        </Typography>
        <Typography 
          variant="body1" 
          color="text.secondary" 
          sx={{ mb: 4, maxWidth: 600, mx: 'auto' }}
        >
          Select the plan that best fits your organization's security needs. 
          All plans include 24/7 support and can be upgraded at any time.
        </Typography>
      </Box>

      {/* Tier Cards */}
      <Grid container spacing={4} justifyContent="center">
        {tiers.map((tier) => (
          <EnhancedTierCard
            key={tier.id}
            tier={tier}
            isInCart={isInCart(tier.id)}
            onAddToCart={onAddToCart}
            onRemoveFromCart={onRemoveFromCart}
            getTierColor={getTierColor}
            getTierIcon={getTierIcon}
            getTierDescription={getTierDescription}
            theme={theme}
          />
        ))}
      </Grid>

      {/* Bottom Info Section */}
      <Box sx={{ textAlign: 'center', mt: 6 }}>
        <Paper
          elevation={1}
          sx={{
            p: 4,
            bgcolor: 'grey.50',
            borderRadius: 2
          }}
        >
          <Typography variant="h6" fontWeight="bold" color="text.primary" sx={{ mb: 2 }}>
            Why Choose Our Security Solutions?
          </Typography>

          <Stack 
            direction={{ xs: 'column', sm: 'row' }} 
            spacing={4} 
            justifyContent="center"
            alignItems="center"
          >
            <Box sx={{ textAlign: 'center' }}>
              <Typography variant="h4" fontWeight="bold" color="primary.main">
                99.9%
              </Typography>
              <Typography variant="body2" color="text.secondary">
                System Uptime
              </Typography>
            </Box>

            <Box sx={{ textAlign: 'center' }}>
              <Typography variant="h4" fontWeight="bold" color="primary.main">
                24/7
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Expert Support
              </Typography>
            </Box>

            <Box sx={{ textAlign: 'center' }}>
              <Typography variant="h4" fontWeight="bold" color="primary.main">
                10,000+
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Trusted Clients
              </Typography>
            </Box>
          </Stack>
        </Paper>
      </Box>
    </Container>
  );
};

export default TierGrid;
