import React from 'react';
import {
  Card,
  CardContent,
  Stack,
  Typography,
  Avatar,
  Button
} from '@mui/material';
import { ChevronRight } from 'lucide-react';

const SummaryCard = ({ 
  title, 
  value, 
  icon, 
  color = 'primary', 
  subtitle, 
  subtitleIcon,
  buttonText,
  onButtonClick
}) => {
  return (
    <Card 
      elevation={0} 
      sx={{ 
        background: 'linear-gradient(135deg, #ffffff 0%, #f8fafc 100%)',
        border: '1px solid', 
        borderColor: 'divider',
        borderRadius: 3,
        position: 'relative',
        overflow: 'hidden',
        transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
        '&:hover': {
          transform: 'translateY(-8px) scale(1.02)',
          boxShadow: '0 20px 40px rgba(0,0,0,0.1), 0 0 0 1px rgba(103, 126, 234, 0.1)',
          borderColor: `${color}.main`,
          '&::before': {
            opacity: 1
          }
        },
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          height: '4px',
          background: color === 'primary' 
            ? 'linear-gradient(90deg, #667eea, #764ba2)' 
            : color === 'success' 
              ? 'linear-gradient(90deg, #4ade80, #22c55e)' 
              : color === 'warning' 
                ? 'linear-gradient(90deg, #fbbf24, #f59e0b)' 
                : 'linear-gradient(90deg, #f87171, #ef4444)',
          opacity: 0,
          transition: 'opacity 0.3s ease'
        }
      }}
    >
      <CardContent>
        <Stack direction="row" justifyContent="space-between" alignItems="center" sx={{ mb: 2 }}>
          <Typography color="text.secondary" variant="subtitle2">
            {title}
          </Typography>
          <Avatar 
            sx={{ 
              background: color === 'primary' 
                ? 'linear-gradient(135deg, #667eea, #764ba2)' 
                : color === 'success' 
                  ? 'linear-gradient(135deg, #4ade80, #22c55e)' 
                  : color === 'warning' 
                    ? 'linear-gradient(135deg, #fbbf24, #f59e0b)' 
                    : 'linear-gradient(135deg, #f87171, #ef4444)',
              width: 56, 
              height: 56,
              boxShadow: color === 'primary' 
                ? '0 8px 24px rgba(103, 126, 234, 0.25)' 
                : color === 'success' 
                  ? '0 8px 24px rgba(76, 175, 80, 0.25)' 
                  : color === 'warning' 
                    ? '0 8px 24px rgba(255, 152, 0, 0.25)' 
                    : '0 8px 24px rgba(244, 67, 54, 0.25)',
              transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
              '&:hover': {
                transform: 'scale(1.15) rotate(5deg)',
                boxShadow: color === 'primary' 
                  ? '0 12px 32px rgba(103, 126, 234, 0.4)' 
                  : color === 'success' 
                    ? '0 12px 32px rgba(76, 175, 80, 0.4)' 
                    : color === 'warning' 
                      ? '0 12px 32px rgba(255, 152, 0, 0.4)' 
                      : '0 12px 32px rgba(244, 67, 54, 0.4)'
              }
            }}
          >
            {icon}
          </Avatar>
        </Stack>
        <Typography 
          variant="h4" 
          fontWeight="bold" 
          sx={{ 
            background: color === 'primary' 
              ? 'linear-gradient(135deg, #667eea, #764ba2)' 
              : color === 'success' 
                ? 'linear-gradient(135deg, #4ade80, #22c55e)' 
                : color === 'warning' 
                  ? 'linear-gradient(135deg, #fbbf24, #f59e0b)' 
                  : 'linear-gradient(135deg, #f87171, #ef4444)',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            mb: 1
          }}
        >
          {value}
        </Typography>

        {subtitle && (
          <Stack direction="row" spacing={1} alignItems="center" sx={{ mt: 1 }}>
            {subtitleIcon}
            <Typography variant={buttonText ? "caption" : "body2"} color={buttonText ? "text.secondary" : `${color}.main`}>
              {subtitle}
            </Typography>
          </Stack>
        )}

        {buttonText && (
          <Button 
            variant="text" 
            size="small" 
            endIcon={<ChevronRight size={16} />}
            onClick={onButtonClick}
            sx={{ 
              mt: 1, 
              p: 0,
              transition: 'all 0.2s ease',
              '&:hover': {
                transform: 'translateX(5px)',
                color: `${color}.main`
              }
            }}
          >
            {buttonText}
          </Button>
        )}
      </CardContent>
    </Card>
  );
};

export default SummaryCard;
