import React, { useEffect } from 'react';
import {
  Box,
  Typography,
  Grid,
  List,
  Divider,
  Button,
  Stack,
  CircularProgress
} from '@mui/material';
import {
  AlertCircle,
  CheckCircle,
  Clock,
  TrendingUp,
  DollarSign,
  ArrowUpRight,
} from 'lucide-react';
import { getCustomerDashboard } from '../../store/customers/customerThunk.ts';
import { useAppDispatch, useAppSelector } from '../../store/hooks.ts';
import SummaryCard from './components/SummaryCard.tsx';
import SectionCard from './components/SectionCard.tsx';
import SubscriptionItem from './components/SubscriptionItem.tsx';
import BundleCard from './components/BundleCard.tsx';

export const formatCurrency = (amount) => {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 2
  }).format(amount);
};

const Dashboard = () => {
  const dispatch = useAppDispatch();

  // Get dashboard data from Redux store
  const { dashboard, isLoading, error } = useAppSelector((state) => (state.customers));

  // Fetch dashboard data on component mount
  useEffect(() => {
    dispatch(getCustomerDashboard());
  }, [dispatch]);

  // If loading, show loading spinner
  if (isLoading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  // If error, show error message
  if (error) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Typography color="error">Error loading dashboard: {error}</Typography>
      </Box>
    );
  }

  // Get customer data
  const { customer, total_spent, subscriptions, latest_bundles } = dashboard;

  // Calculate subscription stats
  const activeSubscriptions = subscriptions.filter(sub => sub.status === 'active').length;
  const expiringSubscriptions = subscriptions.filter(sub => 
    sub.status === 'active' && sub.days_remaining <= 15
  ).length;
  const inactiveSubscriptions = subscriptions.filter(sub => sub.status !== 'active').length;

  return (
    <Box sx={{ 
      width: '100%', 
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%)',
      py: { xs: 3, md: 4 }, 
      px: { xs: 2, md: 4 } 
    }}>
      {/* Professional Header Section */}
      <Box sx={{ 
        mb: 6,
        p: 4,
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        borderRadius: 3,
        color: 'white',
        position: 'relative',
        overflow: 'hidden',
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'url("data:image/svg+xml,%3Csvg width="60" height="60" viewBox="0 0 60 60" xmlns="http://www.w3.org/2000/svg"%3E%3Cg fill="none" fill-rule="evenodd"%3E%3Cg fill="%23ffffff" fill-opacity="0.05"%3E%3Ccircle cx="30" cy="30" r="4"/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")',
          opacity: 0.3
        }
      }}>
        <Box sx={{ position: 'relative', zIndex: 1 }}>
          <Typography variant="h3" component="h1" fontWeight="bold" sx={{ mb: 2 }}>
            Welcome back, {customer?.name || 'Partner'}
          </Typography>
          <Typography variant="h6" sx={{ opacity: 0.9, mb: 3 }}>
            Here's an overview of your security solutions and subscriptions
          </Typography>
          <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2}>
            <Button 
              variant="contained" 
              sx={{ 
                bgcolor: 'rgba(255,255,255,0.2)', 
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(255,255,255,0.3)',
                '&:hover': { 
                  bgcolor: 'rgba(255,255,255,0.3)',
                  transform: 'translateY(-2px)'
                }
              }}
            >
              Explore New Solutions
            </Button>
            <Button 
              variant="outlined" 
              sx={{ 
                borderColor: 'rgba(255,255,255,0.5)', 
                color: 'white',
                '&:hover': { 
                  borderColor: 'white',
                  bgcolor: 'rgba(255,255,255,0.1)'
                }
              }}
            >
              View All Plans
            </Button>
          </Stack>
        </Box>
      </Box>

      {/* Summary Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} lg={3}>
          <SummaryCard 
            title="Total Spent"
            value={formatCurrency(total_spent)}
            icon={<DollarSign size={20} />}
            color="primary"
            subtitle={`Customer: ${customer?.name || 'N/A'}`}
            subtitleIcon={<TrendingUp size={16} />}
          />
        </Grid>

        <Grid item xs={12} sm={6} lg={3}>
          <SummaryCard 
            title="Active Plans"
            value={activeSubscriptions}
            icon={<CheckCircle size={20} />}
            color="success"
            subtitle={`From ${subscriptions.length} total subscriptions`}
          />
        </Grid>

        <Grid item xs={12} sm={6} lg={3}>
          <SummaryCard 
            title="Expiring Soon"
            value={expiringSubscriptions}
            icon={<Clock size={20} />}
            color="warning"
            subtitle="Plans expiring within 15 days"
          />
        </Grid>

        <Grid item xs={12} sm={6} lg={3}>
          <SummaryCard 
            title="Inactive Plans"
            value={inactiveSubscriptions}
            icon={<AlertCircle size={20} />}
            color="error"
            buttonText="Renew Now"
            onButtonClick={() => console.log('Renew now clicked')}
          />
        </Grid>
      </Grid>

      {/* Main Content Grid */}
      <Grid container spacing={4}>
        {/* Subscriptions List */}
        <Grid item xs={12} lg={8}>
          <SectionCard 
            title="Your Plans"
            actionText="View All"
            onActionClick={() => console.log('View all plans clicked')}
          >
            <List sx={{ width: '100%' }}>
              {subscriptions.map((subscription) => (
                <React.Fragment key={subscription.id}>
                  <SubscriptionItem 
                    subscription={subscription}
                    onClick={() => console.log('Subscription clicked', subscription.id)}
                  />
                  <Divider variant="inset" component="li" sx={{ ml: 0 }} />
                </React.Fragment>
              ))}
            </List>
          </SectionCard>
        </Grid>

        {/* Latest Bundles */}
        <Grid item xs={12} lg={4}>
          <SectionCard 
            title="Latest Bundles"
            actionText="Explore"
            onActionClick={() => console.log('Explore bundles clicked')}
          >
            <Stack spacing={2}>
              {latest_bundles.map((bundle, index) => (
                <BundleCard 
                  key={bundle.id}
                  bundle={bundle}
                  index={index}
                  formatCurrency={formatCurrency}
                />
              ))}

              <Button 
                variant="outlined" 
                fullWidth 
                sx={{ 
                  mt: 2,
                  transition: 'all 0.3s ease',
                  '&:hover': {
                    transform: 'translateY(-3px)',
                    boxShadow: (theme) => theme.shadows[2],
                    bgcolor: 'primary.main',
                    color: 'white'
                  }
                }}
                endIcon={<ArrowUpRight size={16} />}
              >
                View All Bundles
              </Button>
            </Stack>
          </SectionCard>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;
