import React, { useState } from 'react';
import { 
  Card, 
  CardContent, 
  Typography, 
  Grid, 
  Box, 
  Chip, 
  Button, 
  Avatar, 
  Stack,
  Divider,
  IconButton,
  Tooltip
} from '@mui/material';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Check, 
  Star, 
  TrendingUp, 
  Shield, 
  Zap, 
  Crown,
  ArrowRight,
  Sparkles,
  Award,
  Target,
  ShoppingCart,
  X
} from 'lucide-react';

const EnhancedTierCard = ({ 
  tier, 
  isInCart, 
  onAddToCart, 
  onRemoveFromCart,
  getTierColor,
  getTierIcon,
  getTierDescription,
  theme,
  index = 0
}) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isExpanded, setIsExpanded] = useState(false);

  const getTierIconComponent = (tierName) => {
    const name = tierName.toLowerCase();
    if (name.includes('core')) return <Shield size={32} />;
    if (name.includes('plus')) return <Zap size={32} />;
    if (name.includes('max')) return <Crown size={32} />;
    return <Target size={32} />;
  };

  const getTierBadgeColor = (tierName) => {
    const name = tierName.toLowerCase();
    if (name.includes('core')) return 'primary';
    if (name.includes('plus')) return 'secondary';
    if (name.includes('max')) return 'error';
    return 'default';
  };

  const getTierGradient = (tierName) => {
    const name = tierName.toLowerCase();
    if (name.includes('core')) return 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)';
    if (name.includes('plus')) return 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)';
    if (name.includes('max')) return 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)';
    return 'linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)';
  };

  const getPopularBadge = (tierName) => {
    const name = tierName.toLowerCase();
    if (name.includes('plus')) return 'Most Popular';
    if (name.includes('max')) return 'Enterprise';
    if (name.includes('core')) return 'Essential';
    return null;
  };

  const cardVariants = {
    hidden: { 
      opacity: 0, 
      y: 50,
      scale: 0.9
    },
    visible: { 
      opacity: 1, 
      y: 0,
      scale: 1,
      transition: {
        duration: 0.6,
        delay: index * 0.15,
        ease: "easeOut"
      }
    },
    hover: {
      y: -12,
      scale: 1.03,
      transition: {
        duration: 0.3,
        ease: "easeInOut"
      }
    }
  };

  const featureVariants = {
    hidden: { opacity: 0, x: -20 },
    visible: { 
      opacity: 1, 
      x: 0,
      transition: {
        duration: 0.5,
        ease: "easeOut"
      }
    }
  };

  const priceVariants = {
    hidden: { opacity: 0, scale: 0.8 },
    visible: { 
      opacity: 1, 
      scale: 1,
      transition: {
        duration: 0.4,
        delay: 0.2,
        ease: "easeOut"
      }
    }
  };

  const isPopular = tier.name.toLowerCase().includes('plus');
  const price = parseFloat(tier.price.replace(/[^0-9.]/g, ''));

  return (
    <Grid item xs={12} sm={6} md={4}>
      <motion.div
        variants={cardVariants}
        initial="hidden"
        animate="visible"
        whileHover="hover"
        onHoverStart={() => setIsHovered(true)}
        onHoverEnd={() => setIsHovered(false)}
      >
        <Card 
          elevation={isInCart ? 12 : 6}
          sx={{
            borderRadius: 4,
            height: '100%',
            display: 'flex',
            flexDirection: 'column',
            overflow: 'hidden',
            border: '2px solid',
            borderColor: isPopular ? '#ff4081' : (isInCart ? 'primary.main' : '#333'),
            background: '#1a1a1a',
            position: 'relative',
            cursor: 'pointer',
            boxShadow: isPopular 
              ? '0 8px 32px rgba(255, 64, 129, 0.3)' 
              : '0 4px 20px rgba(0,0,0,0.3)',
            '&::before': {
              content: '""',
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              height: '4px',
              background: getTierGradient(tier.name),
              zIndex: 1
            }
          }}
          onClick={() => setIsExpanded(!isExpanded)}
        >
          {/* Popular Badge */}
          {isPopular && (
            <motion.div
              initial={{ opacity: 0, scale: 0 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ delay: 0.5, duration: 0.3 }}
            >
              <Chip
                label="Most Popular"
                size="small"
                icon={<Star size={16} />}
                sx={{
                  position: 'absolute',
                  top: -10,
                  left: '50%',
                  transform: 'translateX(-50%)',
                  zIndex: 2,
                  bgcolor: '#ff4081',
                  color: 'white',
                  fontWeight: 'bold',
                  fontSize: '0.75rem',
                  px: 2,
                  boxShadow: 4
                }}
              />
            </motion.div>
          )}

          {/* Header Section */}
          <Box sx={{
            p: 4,
            background: getTierGradient(tier.name),
            color: 'white',
            position: 'relative',
            overflow: 'hidden',
            textAlign: 'center'
          }}>
            <motion.div
              variants={priceVariants}
              initial="hidden"
              animate="visible"
            >
              <Box sx={{
                width: 80,
                height: 80,
                borderRadius: '50%',
                bgcolor: 'rgba(255,255,255,0.2)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                mx: 'auto',
                mb: 2,
                backdropFilter: 'blur(10px)',
                border: '3px solid rgba(255,255,255,0.3)'
              }}>
                {getTierIconComponent(tier.name)}
              </Box>

              <Typography variant="h4" fontWeight="bold" sx={{ mb: 1 }}>
                {tier.name}
              </Typography>

              <Typography variant="body2" sx={{ opacity: 0.9, mb: 3 }}>
                {getTierDescription(tier.name)}
              </Typography>

              <Typography variant="h2" fontWeight="bold" sx={{ mb: 1 }}>
                ${price.toFixed(0)}
              </Typography>

              <Typography variant="body1" sx={{ opacity: 0.9 }}>
                per month
              </Typography>
            </motion.div>

            {/* Animated background elements */}
            <motion.div
              animate={{
                rotate: isHovered ? 360 : 0,
                scale: isHovered ? 1.2 : 1
              }}
              transition={{ duration: 3, ease: "linear", repeat: isHovered ? Infinity : 0 }}
              style={{
                position: 'absolute',
                top: -30,
                right: -30,
                width: 100,
                height: 100,
                background: 'rgba(255,255,255,0.1)',
                borderRadius: '50%',
                zIndex: 0
              }}
            />
          </Box>

          {/* Features Section */}
          <CardContent sx={{ 
            flex: 1, 
            p: 3, 
            bgcolor: '#1a1a1a', 
            color: 'white',
            display: 'flex',
            flexDirection: 'column'
          }}>
            <Typography variant="h6" fontWeight="bold" color="white" sx={{ mb: 3 }}>
              <Sparkles size={20} style={{ marginRight: 8, verticalAlign: 'middle' }} />
              Included Features
            </Typography>

            <AnimatePresence>
              {isExpanded ? (
                <motion.div
                  initial={{ opacity: 0, height: 0 }}
                  animate={{ opacity: 1, height: 'auto' }}
                  exit={{ opacity: 0, height: 0 }}
                  transition={{ duration: 0.3 }}
                >
                  <Stack spacing={2}>
                    {tier.features.map((feature, index) => (
                      <motion.div
                        key={index}
                        variants={featureVariants}
                        initial="hidden"
                        animate="visible"
                        transition={{ delay: index * 0.1 }}
                      >
                        <Box sx={{
                          display: 'flex',
                          alignItems: 'flex-start',
                          p: 1.5,
                          borderRadius: 2,
                          bgcolor: 'rgba(255,255,255,0.05)',
                          border: '1px solid rgba(255,255,255,0.1)',
                          transition: 'all 0.2s',
                          '&:hover': {
                            bgcolor: 'rgba(255,255,255,0.1)',
                            transform: 'translateX(4px)'
                          }
                        }}>
                          <Check 
                            size={18} 
                            color="#4ade80"
                            style={{ marginRight: 12, marginTop: 2, flexShrink: 0 }}
                          />
                          <Typography variant="body2" color="white" sx={{ lineHeight: 1.5 }}>
                            {feature}
                          </Typography>
                        </Box>
                      </motion.div>
                    ))}
                  </Stack>
                </motion.div>
              ) : (
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  exit={{ opacity: 0 }}
                >
                  <Stack spacing={1.5}>
                    {tier.features.slice(0, 4).map((feature, index) => (
                      <motion.div
                        key={index}
                        variants={featureVariants}
                        initial="hidden"
                        animate="visible"
                        transition={{ delay: index * 0.1 }}
                      >
                        <Box sx={{
                          display: 'flex',
                          alignItems: 'center',
                          p: 1
                        }}>
                          <Check 
                            size={16} 
                            color="#4ade80"
                            style={{ marginRight: 8, flexShrink: 0 }}
                          />
                          <Typography variant="body2" color="#ccc" sx={{ 
                            overflow: 'hidden',
                            textOverflow: 'ellipsis',
                            whiteSpace: 'nowrap'
                          }}>
                            {feature}
                          </Typography>
                        </Box>
                      </motion.div>
                    ))}
                    {tier.features.length > 4 && (
                      <Typography variant="body2" color="#4ade80" sx={{ 
                        textAlign: 'center',
                        cursor: 'pointer',
                        fontWeight: 'medium',
                        mt: 1
                      }}>
                        +{tier.features.length - 4} more features
                      </Typography>
                    )}
                  </Stack>
                </motion.div>
              )}
            </AnimatePresence>

            {/* Benefits Section */}
            {tier.benefits && (
              <Box sx={{ mt: 3 }}>
                <Divider sx={{ bgcolor: '#333', mb: 2 }} />
                <Typography variant="subtitle2" fontWeight="bold" color="white" sx={{ mb: 2 }}>
                  <TrendingUp size={16} style={{ marginRight: 8, verticalAlign: 'middle' }} />
                  Key Benefits
                </Typography>
                <Stack spacing={1}>
                  {tier.benefits.map((benefit, index) => (
                    <motion.div
                      key={index}
                      initial={{ opacity: 0, x: -10 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: 0.5 + index * 0.1 }}
                    >
                      <Box sx={{
                        display: 'flex',
                        alignItems: 'center',
                        p: 1,
                        borderRadius: 1,
                        bgcolor: 'rgba(76, 175, 80, 0.2)',
                        border: '1px solid rgba(76, 175, 80, 0.3)'
                      }}>
                        <Award size={14} style={{ marginRight: 8, flexShrink: 0 }} color="#4ade80" />
                        <Typography variant="body2" sx={{ fontSize: '0.875rem', color: '#4ade80' }}>
                          {benefit}
                        </Typography>
                      </Box>
                    </motion.div>
                  ))}
                </Stack>
              </Box>
            )}

            {/* Action Button */}
            <Box sx={{ mt: 'auto', pt: 3 }}>
              <motion.div
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <Button
                  fullWidth
                  variant={isInCart ? "outlined" : "contained"}
                  size="large"
                  onClick={(e) => {
                    e.stopPropagation();
                    if (isInCart) {
                      onRemoveFromCart(tier.id);
                    } else {
                      onAddToCart(tier);
                    }
                  }}
                  startIcon={isInCart ? <X size={20} /> : <ShoppingCart size={20} />}
                  sx={{
                    borderRadius: 3,
                    py: 1.5,
                    fontWeight: 'bold',
                    textTransform: 'none',
                    fontSize: '1rem',
                    background: isInCart ? 'transparent' : getTierGradient(tier.name),
                    border: isInCart ? '2px solid #ff4444' : 'none',
                    color: isInCart ? '#ff4444' : 'white',
                    '&:hover': {
                      background: isInCart ? '#ff4444' : getTierGradient(tier.name),
                      color: 'white',
                      transform: 'translateY(-2px)',
                      boxShadow: 6
                    }
                  }}
                >
                  {isInCart ? 'Remove from Cart' : 'Add to Cart'}
                </Button>
              </motion.div>

              <Typography variant="caption" sx={{ 
                color: '#888', 
                mt: 2, 
                display: 'block', 
                textAlign: 'center',
                fontSize: '0.75rem'
              }}>
                30-day free trial • Cancel anytime
              </Typography>
            </Box>
          </CardContent>
        </Card>
      </motion.div>
    </Grid>
  );
};

export default EnhancedTierCard;
