import React from 'react';
import {
  Card,
  CardContent,
  Box,
  Typography,
  Chip,
  IconButton,
  Button,
  Stack,
  Divider
} from '@mui/material';
import { Heart, Check } from 'lucide-react';
import { motion } from 'framer-motion';

const ModernBundleCard = ({ 
  product, 
  isInWishlist, 
  onProductSelect, 
  onAddToWishlist,
  getTotalPackages,
  index = 0
}) => {
  const getPriceRange = () => {
    if (!product.sub_bundles || product.sub_bundles.length === 0) {
      return { min: 0, max: 0 };
    }

    const hasValidTiers = product.sub_bundles.some(sb => 
      sb.tiers && sb.tiers.length > 0
    );

    if (!hasValidTiers) {
      return { min: 0, max: 0 };
    }

    const allPrices = product.sub_bundles
      .flatMap(sb => sb.tiers || [])
      .map(tier => parseFloat(tier.price.replace(/[^0-9.]/g, '')))
      .filter(price => !isNaN(price));

    if (allPrices.length === 0) return { min: 0, max: 0 };

    const minPrice = Math.min(...allPrices);
    const maxPrice = Math.max(...allPrices);

    return { min: minPrice, max: maxPrice };
  };

  const getCardGradient = (index) => {
    const gradients = [
      'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', // Blue to Purple
      'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)', // Pink to Red
      'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)', // Blue to Cyan
      'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)', // Green to Teal
    ];
    return gradients[index % gradients.length];
  };

  const cardVariants = {
    hidden: { 
      opacity: 0, 
      y: 50,
      scale: 0.9
    },
    visible: { 
      opacity: 1, 
      y: 0,
      scale: 1,
      transition: {
        duration: 0.6,
        delay: index * 0.1,
        ease: "easeOut"
      }
    },
    hover: {
      y: -12,
      scale: 1.03,
      transition: {
        duration: 0.3,
        ease: "easeInOut"
      }
    }
  };

  const priceRange = getPriceRange();
  const isPopular = index === 1; // Make second card popular

  return (
    <motion.div
      variants={cardVariants}
      initial="hidden"
      animate="visible"
      whileHover="hover"
      style={{ height: '100%' }}
    >
      <Card 
        sx={{ 
          height: '100%', 
          display: 'flex', 
          flexDirection: 'column',
          cursor: 'pointer',
          borderRadius: 4,
          background: '#1a1a1a',
          border: isPopular ? '2px solid #ff4081' : '1px solid #333',
          overflow: 'hidden',
          position: 'relative',
          boxShadow: isPopular ? '0 8px 32px rgba(255, 64, 129, 0.3)' : '0 4px 20px rgba(0,0,0,0.3)',
        }}
        onClick={() => onProductSelect(product.id)}
      >
        {/* Popular Badge */}
        {isPopular && (
          <motion.div
            initial={{ opacity: 0, scale: 0 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ delay: 0.5, duration: 0.3 }}
          >
            <Chip
              label="Most Popular"
              size="small"
              sx={{
                position: 'absolute',
                top: -10,
                left: '50%',
                transform: 'translateX(-50%)',
                zIndex: 2,
                bgcolor: '#ff4081',
                color: 'white',
                fontWeight: 'bold',
                fontSize: '0.75rem',
                px: 2
              }}
            />
          </motion.div>
        )}

        {/* Wishlist button */}
        <IconButton
          size="small"
          sx={{
            position: 'absolute',
            top: 16,
            right: 16,
            zIndex: 2,
            bgcolor: 'rgba(255, 255, 255, 0.1)',
            '&:hover': {
              bgcolor: 'rgba(255, 255, 255, 0.2)'
            }
          }}
          onClick={(e) => onAddToWishlist(e, product)}
        >
          <Heart
            size={18}
            fill={isInWishlist(product.id) ? '#ff4081' : 'none'}
            color={isInWishlist(product.id) ? '#ff4081' : '#ccc'}
          />
        </IconButton>

        {/* Header with gradient */}
        <Box
          sx={{
            p: 3,
            background: getCardGradient(index),
            color: 'white',
            textAlign: 'center',
            position: 'relative'
          }}
        >
          <Box sx={{ fontSize: '2rem', mb: 2 }}>
            {product.icon}
          </Box>

          <Typography variant="h5" fontWeight="bold" sx={{ mb: 1 }}>
            {product.name.replace(' SUITE', '')}
          </Typography>

          <Typography variant="body2" sx={{ opacity: 0.9, mb: 2 }}>
            {product.sub_bundles?.length || 0} packages
          </Typography>
        </Box>

        <CardContent sx={{ 
          flexGrow: 1, 
          display: 'flex', 
          flexDirection: 'column', 
          p: 3,
          bgcolor: '#1a1a1a',
          color: 'white'
        }}>
          {/* Monthly Price */}
          <Box sx={{ mb: 3 }}>
            <Typography variant="body2" color="#aaa" sx={{ mb: 1 }}>
              Monthly price
            </Typography>
            <Typography variant="h4" fontWeight="bold" color="white">
              ₹{priceRange.min.toFixed(0)}
            </Typography>
            {priceRange.max > priceRange.min && (
              <Typography variant="body2" color="#aaa">
                Starting from ₹{priceRange.min} - ₹{priceRange.max}
              </Typography>
            )}
          </Box>

          <Divider sx={{ bgcolor: '#333', mb: 3 }} />

          {/* Description */}
          <Typography 
            variant="body2" 
            color="#bbb" 
            sx={{ 
              mb: 3,
              minHeight: '3em',
              lineHeight: 1.5
            }}
          >
            {product.description}
          </Typography>

          {/* Features List */}
          <Box sx={{ mb: 3, flexGrow: 1 }}>
            <Stack spacing={1.5}>
              {product.highlights?.slice(0, 4).map((highlight, idx) => (
                <motion.div
                  key={idx}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 0.3 + idx * 0.1 }}
                >
                  <Box sx={{ display: 'flex', alignItems: 'flex-start', gap: 1 }}>
                    <Check size={16} color="#4ade80" style={{ marginTop: 2, flexShrink: 0 }} />
                    <Typography variant="body2" color="#ccc" sx={{ fontSize: '0.875rem' }}>
                      {highlight}
                    </Typography>
                  </Box>
                </motion.div>
              ))}
            </Stack>
          </Box>

          {/* Sub-bundles info */}
          <Box sx={{ mb: 3 }}>
            <Typography variant="body2" color="#aaa" sx={{ mb: 1 }}>
              Included packages
            </Typography>
            <Typography variant="body2" color="white" fontWeight="medium">
              {getTotalPackages(product)} security packages
            </Typography>
          </Box>

          {/* Action Button */}
          <motion.div
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <Button
              fullWidth
              variant="contained"
              sx={{
                bgcolor: '#ff4081',
                borderRadius: 3,
                py: 1.5,
                fontWeight: 'bold',
                textTransform: 'none',
                fontSize: '1rem',
                '&:hover': {
                  bgcolor: '#f50057'
                }
              }}
            >
              Try 2 months for ₹{priceRange.min.toFixed(0)}
            </Button>
          </motion.div>

          {/* Footer text */}
          <Typography variant="caption" sx={{ 
            color: '#888', 
            mt: 2, 
            textAlign: 'center',
            fontSize: '0.75rem',
            lineHeight: 1.3
          }}>
            ₹{priceRange.min} for 2 months, then ₹{(priceRange.min * 1.2).toFixed(0)} per month after. 
            Offer only available if you haven't tried Premium before.
          </Typography>
        </CardContent>
      </Card>
    </motion.div>
  );
};

export default ModernBundleCard;
