import React, { useState } from 'react';
import { 
  Card, 
  CardContent, 
  Typography, 
  Grid, 
  Box, 
  Chip, 
  Button, 
  Avatar, 
  Stack,
  LinearProgress,
  Divider,
  IconButton,
  Tooltip
} from '@mui/material';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Check, 
  Star, 
  TrendingUp, 
  Shield, 
  Zap, 
  Crown,
  ArrowRight,
  Sparkles,
  Award,
  Target
} from 'lucide-react';

const EnhancedTierCard = ({ 
  tier, 
  isInCart, 
  onAddToCart, 
  onRemoveFromCart,
  getTierColor,
  getTierIcon,
  getTierDescription,
  theme,
  index = 0
}) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isExpanded, setIsExpanded] = useState(false);

  const getTierIconComponent = (tierName) => {
    const name = tierName.toLowerCase();
    if (name.includes('core')) return <Shield size={24} />;
    if (name.includes('plus')) return <Zap size={24} />;
    if (name.includes('max')) return <Crown size={24} />;
    return <Target size={24} />;
  };

  const getTierBadgeColor = (tierName) => {
    const name = tierName.toLowerCase();
    if (name.includes('core')) return 'primary';
    if (name.includes('plus')) return 'secondary';
    if (name.includes('max')) return 'error';
    return 'default';
  };

  const getTierGradient = (tierName) => {
    const name = tierName.toLowerCase();
    if (name.includes('core')) return 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)';
    if (name.includes('plus')) return 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)';
    if (name.includes('max')) return 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)';
    return 'linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)';
  };

  const cardVariants = {
    hidden: { 
      opacity: 0, 
      y: 50,
      scale: 0.9
    },
    visible: { 
      opacity: 1, 
      y: 0,
      scale: 1,
      transition: {
        duration: 0.6,
        delay: index * 0.1,
        ease: "easeOut"
      }
    },
    hover: {
      y: -8,
      scale: 1.02,
      transition: {
        duration: 0.3,
        ease: "easeInOut"
      }
    }
  };

  const featureVariants = {
    hidden: { opacity: 0, x: -20 },
    visible: { 
      opacity: 1, 
      x: 0,
      transition: {
        duration: 0.5,
        ease: "easeOut"
      }
    }
  };

  const priceVariants = {
    hidden: { opacity: 0, scale: 0.8 },
    visible: { 
      opacity: 1, 
      scale: 1,
      transition: {
        duration: 0.4,
        delay: 0.2,
        ease: "easeOut"
      }
    }
  };

  return (
    <Grid item xs={12} sm={6} md={4}>
      <motion.div
        variants={cardVariants}
        initial="hidden"
        animate="visible"
        whileHover="hover"
        onHoverStart={() => setIsHovered(true)}
        onHoverEnd={() => setIsHovered(false)}
      >
        <Card 
          elevation={isInCart ? 8 : 4}
          sx={{
            borderRadius: 4,
            height: '100%',
            display: 'flex',
            flexDirection: 'column',
            overflow: 'hidden',
            border: '2px solid',
            borderColor: isInCart ? 'primary.main' : 'transparent',
            background: isInCart 
              ? `linear-gradient(135deg, ${theme.palette.primary.light}15 0%, ${theme.palette.primary.main}15 100%)`
              : 'background.paper',
            position: 'relative',
            cursor: 'pointer',
            '&::before': {
              content: '""',
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              height: '4px',
              background: getTierGradient(tier.name),
              zIndex: 1
            }
          }}
          onClick={() => setIsExpanded(!isExpanded)}
        >
          {/* Popular Badge */}
          {tier.badge && (
            <motion.div
              initial={{ opacity: 0, scale: 0 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ delay: 0.5, duration: 0.3 }}
            >
              <Chip
                label={tier.badge}
                color={getTierBadgeColor(tier.name)}
                size="small"
                icon={<Star size={16} />}
                sx={{
                  position: 'absolute',
                  top: 16,
                  right: 16,
                  zIndex: 2,
                  fontWeight: 'bold',
                  boxShadow: 2
                }}
              />
            </motion.div>
          )}

          {/* Header Section */}
          <Box sx={{
            p: 3,
            background: getTierGradient(tier.name),
            color: 'white',
            position: 'relative',
            overflow: 'hidden'
          }}>
            <motion.div
              variants={priceVariants}
              initial="hidden"
              animate="visible"
            >
              <Stack direction="row" alignItems="center" spacing={2} sx={{ mb: 2 }}>
                <Avatar
                  sx={{
                    bgcolor: 'rgba(255,255,255,0.2)',
                    width: 56,
                    height: 56,
                    backdropFilter: 'blur(10px)',
                    border: '2px solid rgba(255,255,255,0.3)'
                  }}
                >
                  {getTierIconComponent(tier.name)}
                </Avatar>
                <Box>
                  <Typography variant="h5" fontWeight="bold" sx={{ mb: 0.5 }}>
                    {tier.name}
                  </Typography>
                  <Typography variant="body2" sx={{ opacity: 0.9 }}>
                    {getTierDescription(tier.name)}
                  </Typography>
                </Box>
              </Stack>

              <Typography variant="h3" fontWeight="bold" sx={{ mb: 1 }}>
                {tier.price}
              </Typography>

              <Typography variant="body2" sx={{ opacity: 0.9 }}>
                per month
              </Typography>
            </motion.div>

            {/* Animated background elements */}
            <motion.div
              animate={{
                rotate: isHovered ? 360 : 0,
                scale: isHovered ? 1.1 : 1
              }}
              transition={{ duration: 2, ease: "linear" }}
              style={{
                position: 'absolute',
                top: -20,
                right: -20,
                width: 80,
                height: 80,
                background: 'rgba(255,255,255,0.1)',
                borderRadius: '50%',
                zIndex: 0
              }}
            />
          </Box>

          {/* Features Section */}
          <CardContent sx={{ flex: 1, p: 3 }}>
            <Typography variant="h6" fontWeight="bold" color="text.primary" sx={{ mb: 3 }}>
              <Sparkles size={20} style={{ marginRight: 8, verticalAlign: 'middle' }} />
              Included Features
            </Typography>

            <AnimatePresence>
              {isExpanded ? (
                <motion.div
                  initial={{ opacity: 0, height: 0 }}
                  animate={{ opacity: 1, height: 'auto' }}
                  exit={{ opacity: 0, height: 0 }}
                  transition={{ duration: 0.3 }}
                >
                  <Stack spacing={2}>
                    {tier.features.map((feature, index) => (
                      <motion.div
                        key={index}
                        variants={featureVariants}
                        initial="hidden"
                        animate="visible"
                        transition={{ delay: index * 0.1 }}
                      >
                        <Box sx={{
                          display: 'flex',
                          alignItems: 'flex-start',
                          p: 1.5,
                          borderRadius: 2,
                          bgcolor: 'action.hover',
                          transition: 'all 0.2s',
                          '&:hover': {
                            bgcolor: 'action.selected',
                            transform: 'translateX(4px)'
                          }
                        }}>
                          <Check 
                            size={18} 
                            color={theme.palette.success.main}
                            style={{ marginRight: 12, marginTop: 2, flexShrink: 0 }}
                          />
                          <Typography variant="body2" color="text.primary" sx={{ lineHeight: 1.5 }}>
                            {feature}
                          </Typography>
                        </Box>
                      </motion.div>
                    ))}
                  </Stack>
                </motion.div>
              ) : (
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  exit={{ opacity: 0 }}
                >
                  <Stack spacing={1.5}>
                    {tier.features.slice(0, 3).map((feature, index) => (
                      <motion.div
                        key={index}
                        variants={featureVariants}
                        initial="hidden"
                        animate="visible"
                        transition={{ delay: index * 0.1 }}
                      >
                        <Box sx={{
                          display: 'flex',
                          alignItems: 'center',
                          p: 1,
                          borderRadius: 1
                        }}>
                          <Check 
                            size={16} 
                            color={theme.palette.success.main}
                            style={{ marginRight: 8, flexShrink: 0 }}
                          />
                          <Typography variant="body2" color="text.secondary" sx={{ 
                            overflow: 'hidden',
                            textOverflow: 'ellipsis',
                            whiteSpace: 'nowrap'
                          }}>
                            {feature}
                          </Typography>
                        </Box>
                      </motion.div>
                    ))}
                    {tier.features.length > 3 && (
                      <Typography variant="body2" color="primary.main" sx={{ 
                        textAlign: 'center',
                        cursor: 'pointer',
                        fontWeight: 'medium'
                      }}>
                        +{tier.features.length - 3} more features
                      </Typography>
                    )}
                  </Stack>
                </motion.div>
              )}
            </AnimatePresence>

            {/* Benefits Section */}
            {tier.benefits && (
              <Box sx={{ mt: 3 }}>
                <Divider sx={{ mb: 2 }} />
                <Typography variant="subtitle2" fontWeight="bold" color="text.primary" sx={{ mb: 2 }}>
                  <TrendingUp size={16} style={{ marginRight: 8, verticalAlign: 'middle' }} />
                  Key Benefits
                </Typography>
                <Stack spacing={1}>
                  {tier.benefits.map((benefit, index) => (
                    <motion.div
                      key={index}
                      initial={{ opacity: 0, x: -10 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: 0.5 + index * 0.1 }}
                    >
                      <Box sx={{
                        display: 'flex',
                        alignItems: 'center',
                        p: 1,
                        borderRadius: 1,
                        bgcolor: 'success.light',
                        color: 'success.contrastText'
                      }}>
                        <Award size={14} style={{ marginRight: 8, flexShrink: 0 }} />
                        <Typography variant="body2" sx={{ fontSize: '0.875rem' }}>
                          {benefit}
                        </Typography>
                      </Box>
                    </motion.div>
                  ))}
                </Stack>
              </Box>
            )}
          </CardContent>

          {/* Action Button */}
          <Box sx={{ p: 3, pt: 0 }}>
            <motion.div
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <Button
                fullWidth
                variant={isInCart ? "outlined" : "contained"}
                size="large"
                onClick={(e) => {
                  e.stopPropagation();
                  if (isInCart) {
                    onRemoveFromCart(tier.id);
                  } else {
                    onAddToCart(tier);
                  }
                }}
                sx={{
                  borderRadius: 3,
                  py: 1.5,
                  fontWeight: 'bold',
                  textTransform: 'none',
                  fontSize: '1rem',
                  background: isInCart ? 'transparent' : getTierGradient(tier.name),
                  border: isInCart ? '2px solid' : 'none',
                  borderColor: 'primary.main',
                  color: isInCart ? 'primary.main' : 'white',
                  '&:hover': {
                    background: isInCart ? 'primary.main' : getTierGradient(tier.name),
                    color: isInCart ? 'white' : 'white',
                    transform: 'translateY(-2px)',
                    boxShadow: 4
                  }
                }}
                endIcon={isInCart ? null : <ArrowRight size={20} />}
              >
                {isInCart ? 'Remove from Cart' : 'Add to Cart'}
              </Button>
            </motion.div>
          </Box>
        </Card>
      </motion.div>
    </Grid>
  );
};

export default EnhancedTierCard;
