import React from 'react';
import { 
  Box, 
  Typography, 
  Paper, 
  Avatar, 
  Stack,
  Rating,
  Chip
} from '@mui/material';
import { motion } from 'framer-motion';
import { 
  Quote, 
  Star, 
  Users, 
  TrendingUp,
  Shield,
  Zap,
  Crown
} from 'lucide-react';

const TestimonialsSection = ({ theme }) => {
  const testimonials = [
    {
      id: 1,
      name: "Sarah Johnson",
      role: "Security Director",
      company: "TechCorp Industries",
      rating: 5,
      tier: "Max",
      avatar: "SJ",
      quote: "The Max tier has transformed our security operations. The AI-powered threat detection is incredible - we've reduced incidents by 95%.",
      benefits: ["95% incident reduction", "24/7 AI monitoring", "Real-time alerts"]
    },
    {
      id: 2,
      name: "Michael Chen",
      role: "IT Manager",
      company: "Global Solutions Ltd",
      rating: 5,
      tier: "Plus",
      avatar: "MC",
      quote: "The Plus plan gives us enterprise-level security without the complexity. The automated response system is a game-changer.",
      benefits: ["Automated responses", "Easy deployment", "Cost effective"]
    },
    {
      id: 3,
      name: "Emily Rodriguez",
      role: "Operations Lead",
      company: "SecureNet Corp",
      rating: 5,
      tier: "Core",
      avatar: "ER",
      quote: "Perfect for our startup. The Core tier provides essential security features that scale with our growth.",
      benefits: ["Essential features", "Scalable", "Budget friendly"]
    }
  ];

  const getTierIcon = (tierName) => {
    const name = tierName.toLowerCase();
    if (name.includes('core')) return <Shield size={16} />;
    if (name.includes('plus')) return <Zap size={16} />;
    if (name.includes('max')) return <Crown size={16} />;
    return <Star size={16} />;
  };

  const getTierColor = (tierName) => {
    const name = tierName.toLowerCase();
    if (name.includes('core')) return 'primary';
    if (name.includes('plus')) return 'secondary';
    if (name.includes('max')) return 'error';
    return 'default';
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.1
      }
    }
  };

  const cardVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  const stats = [
    { label: "Active Users", value: "10,000+", icon: <Users size={24} /> },
    { label: "Success Rate", value: "99.9%", icon: <TrendingUp size={24} /> },
    { label: "Uptime", value: "99.99%", icon: <Shield size={24} /> }
  ];

  return (
    <motion.div
      variants={containerVariants}
      initial="hidden"
      animate="visible"
    >
      <Box sx={{ py: 6 }}>
        {/* Header */}
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <Box sx={{ textAlign: 'center', mb: 6 }}>
            <Typography 
              variant="h3" 
              fontWeight="bold" 
              color="text.primary" 
              sx={{ mb: 2 }}
            >
              Trusted by Industry Leaders
            </Typography>
            <Typography 
              variant="h6" 
              color="text.secondary" 
              sx={{ mb: 4, maxWidth: 600, mx: 'auto' }}
            >
              See how organizations across industries are transforming their security 
              with our advanced AI-powered solutions.
            </Typography>
          </Box>
        </motion.div>

        {/* Stats Section */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          <Box sx={{ 
            display: 'flex', 
            justifyContent: 'center', 
            gap: 6, 
            mb: 6,
            flexWrap: 'wrap'
          }}>
            {stats.map((stat, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ delay: 0.4 + index * 0.1 }}
              >
                <Paper 
                  elevation={2}
                  sx={{ 
                    p: 3, 
                    textAlign: 'center',
                    borderRadius: 3,
                    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                    color: 'white',
                    minWidth: 150
                  }}
                >
                  <Box sx={{ mb: 2 }}>
                    {stat.icon}
                  </Box>
                  <Typography variant="h4" fontWeight="bold" sx={{ mb: 1 }}>
                    {stat.value}
                  </Typography>
                  <Typography variant="body2" sx={{ opacity: 0.9 }}>
                    {stat.label}
                  </Typography>
                </Paper>
              </motion.div>
            ))}
          </Box>
        </motion.div>

        {/* Testimonials Grid */}
        <Box sx={{ 
          display: 'grid', 
          gridTemplateColumns: { xs: '1fr', md: 'repeat(3, 1fr)' },
          gap: 3 
        }}>
          {testimonials.map((testimonial, index) => (
            <motion.div
              key={testimonial.id}
              variants={cardVariants}
            >
              <Paper 
                elevation={3}
                sx={{ 
                  p: 3, 
                  height: '100%',
                  borderRadius: 3,
                  background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
                  border: '1px solid',
                  borderColor: 'divider',
                  position: 'relative',
                  overflow: 'hidden'
                }}
              >
                {/* Quote Icon */}
                <Box sx={{
                  position: 'absolute',
                  top: 16,
                  right: 16,
                  opacity: 0.1,
                  transform: 'scale(2)'
                }}>
                  <Quote size={48} />
                </Box>

                {/* Header */}
                <Stack direction="row" alignItems="center" spacing={2} sx={{ mb: 3 }}>
                  <Avatar
                    sx={{
                      bgcolor: 'primary.main',
                      width: 48,
                      height: 48,
                      fontWeight: 'bold'
                    }}
                  >
                    {testimonial.avatar}
                  </Avatar>
                  <Box sx={{ flex: 1 }}>
                    <Typography variant="h6" fontWeight="bold" color="text.primary">
                      {testimonial.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {testimonial.role} at {testimonial.company}
                    </Typography>
                  </Box>
                  <Chip
                    label={testimonial.tier}
                    color={getTierColor(testimonial.tier)}
                    icon={getTierIcon(testimonial.tier)}
                    size="small"
                    sx={{ fontWeight: 'bold' }}
                  />
                </Stack>

                {/* Rating */}
                <Box sx={{ mb: 2 }}>
                  <Rating value={testimonial.rating} readOnly size="small" />
                </Box>

                {/* Quote */}
                <Typography 
                  variant="body1" 
                  color="text.primary" 
                  sx={{ 
                    mb: 3, 
                    fontStyle: 'italic',
                    lineHeight: 1.6
                  }}
                >
                  "{testimonial.quote}"
                </Typography>

                {/* Benefits */}
                <Stack spacing={1}>
                  {testimonial.benefits.map((benefit, benefitIndex) => (
                    <motion.div
                      key={benefitIndex}
                      initial={{ opacity: 0, x: -10 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: 0.6 + benefitIndex * 0.1 }}
                    >
                      <Chip
                        label={benefit}
                        size="small"
                        sx={{
                          bgcolor: 'success.light',
                          color: 'success.contrastText',
                          fontSize: '0.75rem'
                        }}
                      />
                    </motion.div>
                  ))}
                </Stack>
              </Paper>
            </motion.div>
          ))}
        </Box>

        {/* Bottom CTA */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.8 }}
        >
          <Box sx={{ textAlign: 'center', mt: 6 }}>
            <Typography variant="h5" fontWeight="bold" color="text.primary" sx={{ mb: 2 }}>
              Join Thousands of Satisfied Customers
            </Typography>
            <Typography variant="body1" color="text.secondary" sx={{ mb: 3 }}>
              Experience the same level of security and satisfaction that industry leaders trust.
            </Typography>
            <Typography variant="body2" color="primary.main" fontWeight="medium">
              ✨ 30-day free trial • 🔒 Enterprise-grade security • 🚀 Instant deployment
            </Typography>
          </Box>
        </motion.div>
      </Box>
    </motion.div>
  );
};

export default TestimonialsSection;
